<script setup lang="ts">
import qrCode from "../components/qrCode.vue";
import Toggle from '@vueform/toggle'
//@ts-ignore
import Cookies from "js-cookie"


const props = defineProps(["inRoom", "inSettings", "roomId"])

const config = useRuntimeConfig()
const route = useRoute()
const selectedTheme = ref(true)
const colormode = useColorMode()
const showWrestle = ref(false)
colormode.preference = "light"
const theme = useCookie("theme", { default: () => ("light") })
colormode.preference = theme.value
const settingsLink = ref("/settings?id="  + props.roomId)
const roomLink = ref("/room?id=" + route.query.id)

if (colormode.preference != "light" && colormode.preference != "dark") {
	colormode.preference = "light"
	Cookies.set('theme', colormode.preference)
}

if (colormode.preference == "light") {
	selectedTheme.value = false
} else {
	selectedTheme.value = true
}

function toggleDark() {
	if (colormode.preference == "light") {
		colormode.preference = "dark"
	} else {
		colormode.preference = "light"
	}
	Cookies.set('theme', colormode.preference)
}

const showQRCodeModal = ref(false)

function redirectToRoom(){
	window.location.href = roomLink.value
}
</script>

